package lec2;

import java.util.Scanner;

public class pattern0 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		System.out.print("Enter n: ");
		int n = sc.nextInt();
		int i=0;
		while(i<n) {
			System.out.println("*");
			i++;
			
			// 8
			//13
			//14
			//15
			//16
			//17
			//18
			//19
			//20
			//32
			
		}
	}

}
